import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import './Pages.css';
import axios from 'axios';

const Courses = () => {
  const [materials, setMaterials] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { currentUser } = useAuth();

  useEffect(() => {
    const fetchMaterials = async () => {
      try {
        setLoading(true);
        const response = await axios.get('http://localhost:5001/api/materials');
        setMaterials(response.data);
        setError(null);
      } catch (err) {
        console.error('Error fetching materials:', err);
        setError('Failed to load materials. Please try again later.');
      } finally {
        setLoading(false);
      }
    };

    fetchMaterials();
  }, []);

  if (loading) {
    return (
      <div className="page-container">
        <div className="loading">Loading courses...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="page-container">
        <div className="error-message">
          <span>âš </span> {error}
        </div>
      </div>
    );
  }

  return (
    <div className="page-container">
      <section className="hero">
        <div className="hero-content">
          <h1>Explore Our Learning Materials</h1>
          <p>Access all uploaded study materials and courses</p>
          {currentUser?.role === 'admin' && (
            <Link to="/admin" className="admin-link">
              Go to Admin Panel to Upload Materials
            </Link>
          )}
        </div>
      </section>

      <section className="materials-section">
        {materials.length === 0 ? (
          <div className="no-materials">
            <p>No learning materials available yet. Please check back later.</p>
            {currentUser?.role === 'admin' && (
              <Link to="/admin" className="upload-btn">
                Upload Materials
              </Link>
            )}
          </div>
        ) : (
          <div className="materials-grid">
            {materials.map((material) => (
              <div key={material._id} className="material-card">
                <div className="material-icon">
                  {material.type === 'video' ? 'ðŸŽ¥' : 'ðŸ“„'}
                </div>
                <div className="material-content">
                  <h3>{material.title}</h3>
                  <p className="material-description">{material.description}</p>
                  <div className="material-meta">
                    <span className="type">Type: {material.type}</span>
                    <span className="date">
                      Uploaded: {new Date(material.createdAt).toLocaleDateString()}
                    </span>
                  </div>
                  <a 
                    href={`http://localhost:5001/${material.filePath}`} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="view-btn"
                  >
                    View {material.type === 'video' ? 'Video' : 'Document'}
                  </a>
                </div>
              </div>
            ))}
          </div>
        )}
      </section>
    </div>
  );
};

export default Courses;
