import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import axios from 'axios';
import './AdminPanel.css';

const AdminPanel = () => {
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { currentUser } = useAuth();

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5001/api/users');
      setUsers(response.data);
      setError('');
    } catch (err) {
      console.error('Error fetching users:', err);
      setError('Failed to fetch users. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const getRoleColor = (role) => {
    switch (role) {
      case 'admin':
        return '#e74c3c';
      case 'instructor':
        return '#f39c12';
      case 'student':
        return '#3498db';
      default:
        return '#95a5a6';
    }
  };

  if (loading) {
    return (
      <div className="admin-panel">
        <div className="loading">
          <div className="spinner"></div>
          <p>Loading users...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-panel">
      <div className="admin-header">
        <h1>Admin Panel</h1>
        <p>Manage and monitor user accounts</p>
        <button className="refresh-btn" onClick={fetchUsers}>
          <span>üîÑ</span> Refresh Data
        </button>
      </div>

      {error && (
        <div className="error-message">
          <span>‚ö†Ô∏è</span> {error}
        </div>
      )}

      <div className="stats-cards">
        <div className="stat-card">
          <h3>Total Users</h3>
          <p className="stat-number">{users.length}</p>
        </div>
        <div className="stat-card">
          <h3>Admins</h3>
          <p className="stat-number">{users.filter(u => u.role === 'admin').length}</p>
        </div>
        <div className="stat-card">
          <h3>Instructors</h3>
          <p className="stat-number">{users.filter(u => u.role === 'instructor').length}</p>
        </div>
        <div className="stat-card">
          <h3>Students</h3>
          <p className="stat-number">{users.filter(u => u.role === 'student').length}</p>
        </div>
      </div>

      <div className="users-table-container">
        <h2>User Management</h2>
        {users.length === 0 ? (
          <div className="no-users">
            <p>No users found.</p>
          </div>
        ) : (
          <div className="table-wrapper">
            <table className="users-table">
              <thead>
                <tr>
                  <th>Avatar</th>
                  <th>Display Name</th>
                  <th>Email</th>
                  <th>Role</th>
                  <th>Created At</th>
                  <th>Last Login</th>
                  <th>Enrolled Courses</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {users.map((user) => (
                  <tr key={user.uid}>
                    <td>
                      <div className="user-avatar">
                        {user.photoURL ? (
                          <img src={user.photoURL} alt={user.displayName} />
                        ) : (
                          <div className="avatar-placeholder">
                            {user.displayName?.charAt(0)?.toUpperCase() || 'U'}
                          </div>
                        )}
                      </div>
                    </td>
                    <td>
                      <div className="user-name">
                        <strong>{user.displayName || 'N/A'}</strong>
                        <small>UID: {user.uid.substring(0, 8)}...</small>
                      </div>
                    </td>
                    <td>{user.email}</td>
                    <td>
                      <span 
                        className="role-badge" 
                        style={{ backgroundColor: getRoleColor(user.role) }}
                      >
                        {user.role?.toUpperCase() || 'STUDENT'}
                      </span>
                    </td>
                    <td>{formatDate(user.createdAt)}</td>
                    <td>{formatDate(user.lastLoginAt)}</td>
                    <td>
                      <span className="courses-count">
                        {user.enrolledCourses?.length || 0} courses
                      </span>
                    </td>
                    <td>
                      <span className="status-active">Active</span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
