import React from 'react';
import { Link } from 'react-router-dom';
import './Pages.css';

const Courses = () => {
  // Sample course data - in a real app, this would come from your backend
  const courses = [
    {
      id: 1,
      title: 'Web Development Fundamentals',
      description: 'Learn the basics of HTML, CSS, and JavaScript to build modern web applications.',
      duration: '8 weeks',
      level: 'Beginner',
      image: 'https://via.placeholder.com/300x200?text=Web+Dev'
    },
    {
      id: 2,
      title: 'React Masterclass',
      description: 'Master React.js with hooks, context API, and advanced patterns.',
      duration: '10 weeks',
      level: 'Intermediate',
      image: 'https://via.placeholder.com/300x200?text=React'
    },
    {
      id: 3,
      title: 'Node.js Backend Development',
      description: 'Build scalable server-side applications with Node.js and Express.',
      duration: '12 weeks',
      level: 'Intermediate',
      image: 'https://via.placeholder.com/300x200?text=Node.js'
    },
    {
      id: 4,
      title: 'MongoDB for Developers',
      description: 'Learn NoSQL database design and implementation with MongoDB.',
      duration: '6 weeks',
      level: 'Intermediate',
      image: 'https://via.placeholder.com/300x200?text=MongoDB'
    },
  ];

  return (
    <div className="page-container">
      <section className="hero">
        <div className="hero-content">
          <h1>Explore Our Courses</h1>
          <p>Start your learning journey with our expert-led courses</p>
          <div className="search-bar">
            <input type="text" placeholder="Search courses..." />
            <button>Search</button>
          </div>
        </div>
      </section>

      <section className="courses-grid">
        {courses.map((course) => (
          <div key={course.id} className="course-card">
            <div className="course-image">
              <img src={course.image} alt={course.title} />
            </div>
            <div className="course-info">
              <span className="level-badge">{course.level}</span>
              <h3>{course.title}</h3>
              <p>{course.description}</p>
              <div className="course-meta">
                <span>⏱️ {course.duration}</span>
                <Link to={`/course/${course.id}`} className="enroll-btn">Learn More</Link>
              </div>
            </div>
          </div>
        ))}
      </section>
    </div>
  );
};

export default Courses;
