import React, { useState, useEffect } from 'react';
import { useAuth } from '../../contexts/AuthContext';
import { useNavigate } from 'react-router-dom';
import { 
  BookOpen, 
  Users, 
  Award, 
  TrendingUp, 
  Clock, 
  Star,
  LogOut,
  User,
  Settings
} from 'lucide-react';
import axios from 'axios';
import './Dashboard.css';

export default function Dashboard() {
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();
  const [isAdmin, setIsAdmin] = useState(false);
  const [userRole, setUserRole] = useState('student');

  useEffect(() => {
    const checkUserRole = async () => {
      if (currentUser) {
        try {
          const response = await axios.get(`http://localhost:5001/api/users/${currentUser.uid}`);
          const userData = response.data;
          setUserRole(userData.role || 'student');
          setIsAdmin(userData.role === 'admin');
        } catch (error) {
          console.error('Error fetching user role:', error);
        }
      }
    };

    checkUserRole();
  }, [currentUser]);

  async function handleLogout() {
    try {
      await logout();
      navigate('/login');
    } catch {
      console.error('Failed to log out');
    }
  }

  const courses = [
    {
      id: 1,
      title: 'React Fundamentals',
      progress: 75,
      duration: '8 hours',
      instructor: 'John Doe',
      rating: 4.8
    },
    {
      id: 2,
      title: 'JavaScript Advanced',
      progress: 45,
      duration: '12 hours',
      instructor: 'Jane Smith',
      rating: 4.9
    },
    {
      id: 3,
      title: 'Node.js Backend',
      progress: 20,
      duration: '15 hours',
      instructor: 'Mike Johnson',
      rating: 4.7
    }
  ];

  const stats = [
    { icon: BookOpen, label: 'Courses Enrolled', value: '12' },
    { icon: Award, label: 'Certificates', value: '5' },
    { icon: Clock, label: 'Hours Learned', value: '48' },
    { icon: TrendingUp, label: 'Progress', value: '68%' }
  ];

  return (
    <div className="dashboard">
      <header className="dashboard-header">
        <div className="header-content">
          <div className="user-info">
            <div className="user-avatar">
              {currentUser.photoURL ? (
                <img src={currentUser.photoURL} alt="Profile" />
              ) : (
                <User size={24} />
              )}
            </div>
            <div>
              <h1>Welcome back, {currentUser.displayName || 'Student'}!</h1>
              <p>Continue your learning journey</p>
              {userRole && (
                <span className={`role-badge role-${userRole}`}>
                  {userRole.toUpperCase()}
                </span>
              )}
            </div>
          </div>
          <div className="header-actions">
            {isAdmin && (
              <button 
                onClick={() => navigate('/admin')} 
                className="admin-btn"
                title="Admin Panel"
              >
                <Settings size={20} />
                Admin Panel
              </button>
            )}
            <button onClick={handleLogout} className="logout-btn">
              <LogOut size={20} />
              Logout
            </button>
          </div>
        </div>
      </header>

      <main className="dashboard-main">
        <section className="stats-section">
          <h2>Your Learning Stats</h2>
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <div key={index} className="stat-card">
                <div className="stat-icon">
                  <stat.icon size={24} />
                </div>
                <div className="stat-content">
                  <h3>{stat.value}</h3>
                  <p>{stat.label}</p>
                </div>
              </div>
            ))}
          </div>
        </section>

        <section className="courses-section">
          <div className="section-header">
            <h2>Continue Learning</h2>
            <button className="view-all-btn">View All Courses</button>
          </div>
          <div className="courses-grid">
            {courses.map((course) => (
              <div key={course.id} className="course-card">
                <div className="course-header">
                  <h3>{course.title}</h3>
                  <div className="course-rating">
                    <Star size={16} fill="currentColor" />
                    <span>{course.rating}</span>
                  </div>
                </div>
                <div className="course-meta">
                  <span className="instructor">by {course.instructor}</span>
                  <span className="duration">
                    <Clock size={14} />
                    {course.duration}
                  </span>
                </div>
                <div className="progress-section">
                  <div className="progress-bar">
                    <div 
                      className="progress-fill" 
                      style={{ width: `${course.progress}%` }}
                    ></div>
                  </div>
                  <span className="progress-text">{course.progress}% complete</span>
                </div>
                <button className="continue-btn">Continue Learning</button>
              </div>
            ))}
          </div>
        </section>

        <section className="recommendations-section">
          <h2>Recommended for You</h2>
          <div className="recommendation-cards">
            <div className="recommendation-card">
              <BookOpen size={32} />
              <h3>MongoDB Basics</h3>
              <p>Learn database fundamentals with hands-on projects</p>
              <button className="enroll-btn">Enroll Now</button>
            </div>
            <div className="recommendation-card">
              <Users size={32} />
              <h3>Team Collaboration</h3>
              <p>Master Git and collaborative development workflows</p>
              <button className="enroll-btn">Enroll Now</button>
            </div>
          </div>
        </section>
      </main>
    </div>
  );
}
