import React, { useState, useEffect } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Upload, BookOpen, FileText, Users, BarChart3 } from 'lucide-react';
import FileUpload from '../components/admin/FileUpload';
import CourseUpload from '../components/admin/CourseUpload';
import './InstructorDashboard.css';

const InstructorDashboard = () => {
  const { currentUser } = useAuth();
  const [activeTab, setActiveTab] = useState('overview');
  const [stats, setStats] = useState({
    totalCourses: 0,
    totalMaterials: 0,
    totalStudents: 0
  });

  useEffect(() => {
    // Fetch instructor stats
    fetchInstructorStats();
  }, []);

  const fetchInstructorStats = async () => {
    // TODO: Implement API call to get instructor-specific stats
    setStats({
      totalCourses: 5,
      totalMaterials: 23,
      totalStudents: 156
    });
  };

  const tabs = [
    { id: 'overview', label: 'Overview', icon: BarChart3 },
    { id: 'upload-material', label: 'Upload Material', icon: Upload },
    { id: 'create-course', label: 'Create Course', icon: BookOpen },
    { id: 'my-content', label: 'My Content', icon: FileText }
  ];

  const renderTabContent = () => {
    switch (activeTab) {
      case 'overview':
        return (
          <div className="instructor-overview">
            <h2>Instructor Dashboard</h2>
            <p>Welcome back, {currentUser?.displayName}! Here's your teaching overview.</p>

            <div className="stats-grid">
              <div className="stat-card">
                <div className="stat-icon">
                  <BookOpen size={24} />
                </div>
                <div className="stat-info">
                  <h3>{stats.totalCourses}</h3>
                  <p>Courses Created</p>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-icon">
                  <FileText size={24} />
                </div>
                <div className="stat-info">
                  <h3>{stats.totalMaterials}</h3>
                  <p>Materials Uploaded</p>
                </div>
              </div>

              <div className="stat-card">
                <div className="stat-icon">
                  <Users size={24} />
                </div>
                <div className="stat-info">
                  <h3>{stats.totalStudents}</h3>
                  <p>Students Enrolled</p>
                </div>
              </div>
            </div>

            <div className="recent-activity">
              <h3>Recent Activity</h3>
              <div className="activity-list">
                <div className="activity-item">
                  <FileText size={16} />
                  <span>Uploaded "React Fundamentals.pdf"</span>
                  <span className="activity-time">2 hours ago</span>
                </div>
                <div className="activity-item">
                  <BookOpen size={16} />
                  <span>Created course "Advanced JavaScript"</span>
                  <span className="activity-time">1 day ago</span>
                </div>
                <div className="activity-item">
                  <Users size={16} />
                  <span>5 new students enrolled in your courses</span>
                  <span className="activity-time">2 days ago</span>
                </div>
              </div>
            </div>
          </div>
        );

      case 'upload-material':
        return (
          <div className="upload-section">
            <FileUpload onUploadSuccess={() => {
              // Refresh stats or show success message
              fetchInstructorStats();
            }} />
          </div>
        );

      case 'create-course':
        return (
          <div className="course-section">
            <CourseUpload onUploadSuccess={() => {
              // Refresh stats or show success message
              fetchInstructorStats();
            }} />
          </div>
        );

      case 'my-content':
        return (
          <div className="content-section">
            <h2>My Content</h2>
            <p>View and manage your courses and materials here.</p>
            {/* TODO: Add content management interface */}
            <div className="content-placeholder">
              <FileText size={48} />
              <p>Content management interface coming soon...</p>
            </div>
          </div>
        );

      default:
        return null;
    }
  };

  return (
    <div className="instructor-dashboard">
      <div className="dashboard-header">
        <h1>Instructor Dashboard</h1>
        <div className="user-info">
          <span>Welcome, {currentUser?.displayName}</span>
          <span className="role-badge instructor">Instructor</span>
        </div>
      </div>

      <div className="dashboard-tabs">
        {tabs.map(tab => {
          const IconComponent = tab.icon;
          return (
            <button
              key={tab.id}
              className={`tab-button ${activeTab === tab.id ? 'active' : ''}`}
              onClick={() => setActiveTab(tab.id)}
            >
              <IconComponent size={20} />
              <span>{tab.label}</span>
            </button>
          );
        })}
      </div>

      <div className="dashboard-content">
        {renderTabContent()}
      </div>
    </div>
  );
};

export default InstructorDashboard;
